contracts/interfaces/UniswapV3Interfaces.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;
pragma abicoder v2;

// Compatibility layer for Uniswap V3 interfaces
interface IUniswapV3PoolCompat {
    function token0() external view returns (address);
    function token1() external view returns (address);
    function slot0() external view returns (
        uint160 sqrtPriceX96,
        int24 tick,
        uint16 observationIndex,
        uint16 observationCardinality,
        uint16 observationCardinalityNext,
        uint8 feeProtocol,
        bool unlocked
    );
    function observe(uint32[] calldata secondsAgos) external view returns (
        int56[] memory tickCumulatives,
        uint160[] memory secondsPerLiquidityCumulativeX128s
    );
    function flash(
        address recipient,
        uint256 amount0,
        uint256 amount1,
        bytes calldata data
    ) external;
    function swap(
        address recipient,
        bool zeroForOne,
        int256 amountSpecified,
        uint160 sqrtPriceLimitX96,
        bytes calldata data
    ) external returns (int256 amount0, int256 amount1);
    function mint(
        address recipient,
        int24 tickLower,
        int24 tickUpper,
        uint128 amount,
        bytes calldata data
    ) external returns (uint256 amount0, uint256 amount1);
    function initialize(uint160 sqrtPriceX96) external;
}

interface IUniswapV3FactoryCompat {
    function createPool(
        address tokenA,
        address tokenB,
        uint24 fee
    ) external returns (address pool);
    function getPool(
        address tokenA,
        address tokenB,
        uint24 fee
    ) external view returns (address pool);
}

interface ISwapRouterCompat {
    struct ExactInputSingleParams {
        address tokenIn;
        address tokenOut;
        uint24 fee;
        address recipient;
        uint256 deadline;
        uint256 amountIn;
        uint256 amountOutMinimum;
        uint160 sqrtPriceLimitX96;
    }

    function exactInputSingle(ExactInputSingleParams calldata params) external payable returns (uint256 amountOut);
}

contracts/Lock.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.28;

// Uncomment this line to use console.log
// import "hardhat/console.sol";

contract Lock {
    uint public unlockTime;
    address payable public owner;

    event Withdrawal(uint amount, uint when);

    constructor(uint _unlockTime) payable {
        require(
            block.timestamp < _unlockTime,
            "Unlock time should be in the future"
        );

        unlockTime = _unlockTime;
        owner = payable(msg.sender);
    }

    function withdraw() public {
        // Uncomment this line, and the import of "hardhat/console.sol", to print a log in your terminal
        // console.log("Unlock time is %o and block timestamp is %o", unlockTime, block.timestamp);

        require(block.timestamp >= unlockTime, "You can't withdraw yet");
        require(msg.sender == owner, "You aren't the owner");

        emit Withdrawal(address(this).balance, block.timestamp);

        owner.transfer(address(this).balance);
    }
}

contracts/mocks/FlashBorrower.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "../interfaces/UniswapV3InterfacesCompat.sol";

/**
 * @title FlashBorrower
 * @dev Contract that tests flash loan functionality in Uniswap V3
 * This contract is used for testing purposes to verify Uniswap's flash loan mechanism
 */
contract FlashBorrower {
    IUniswapV3PoolCompat public pool;
    bool public shouldRepay;
    
    constructor(address _pool) {
        pool = IUniswapV3PoolCompat(_pool);
    }
    
    /**
     * @dev Executes a flash loan
     * @param amount0 The amount of token0 to borrow
     * @param amount1 The amount of token1 to borrow
     * @param repayProperly Whether to repay the flash loan properly
     */
    function flashBorrow(uint256 amount0, uint256 amount1, bool repayProperly) external {
        shouldRepay = repayProperly;
        pool.flash(address(this), amount0, amount1, abi.encode(amount0, amount1));
    }
    
    /**
     * @dev Callback for the flash function
     */
    function uniswapV3FlashCallback(uint256 fee0, uint256 fee1, bytes calldata data) external {
        require(msg.sender == address(pool), "Not authorized");
        
        // Decode the flash loan parameters
        (uint256 amount0, uint256 amount1) = abi.decode(data, (uint256, uint256));
        
        // Repay the flash loan if shouldRepay is true
        if (shouldRepay) {
            address token0 = pool.token0();
            address token1 = pool.token1();
            
            if (amount0 > 0) {
                IERC20(token0).transfer(address(pool), amount0 + fee0);
            }
            if (amount1 > 0) {
                IERC20(token1).transfer(address(pool), amount1 + fee1);
            }
        }
        // If shouldRepay is false, the transaction will revert as we don't repay
    }
}

